import React, { Component, useState } from 'react';
import styled from 'styled-components';
import ReactPaginate from 'react-paginate';
import './stylesMain.css';
import axios from 'axios';

const Main = styled.section`
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:center;
    font-family:'Calibri';
    background:url('./Media/form-template.jpg');
    width:100vw;
    height:auto;
    background:#112337;
`
const Header = styled.header`
& {
    display:flex;
    align-items:center;
    justify-content:flex-start;
    font-family:'Calibri';
    background:#112337;
    width:100vw;
    height:100px;
}
& h1 {
    font-size:3rem;
    width:500px;
    display:flex;
    justify-content:center;
    align-items:center;
    color:#fff;
    text-transform:uppercase;
    letter-spacing:-.2rem;
}
`
const FormContainerAll = styled.div`
& {
    width:100vw;
    background:#112337;
    height:auto;
    display:flex;
    flex-direction:row;
}`
const FormContainerLeft = styled.div`
& {
    width:20vw;
    height:auto;
}
& img {
    width:100%;
    height:90vh;
    object-fit:cover;
    object-position:center;

}
`
const FormContainerRight = styled.div`
& {
    width:80vw;
    height:100vh;
    background:#112337;
    overflow:scroll;
    background:red;
}
`
const ResultsTable= styled.table`
& {
    display:table;
    font-family:'Calibri';
    background:#ffff;
    width:20vw;
    height:auto;
}
& th {
    background:black;
    color:white;
    padding:10px;
    font-size:1.3rem;
    text-transform:uppercase;
    border:0px;
    }
& td {
    font-size:1.1rem;
    align-items:center;
    text-align:center;
    padding:10px;
    border:0px;
    font-weight:bold;
}
& tr:nth-of-type(odd) {
    background:#fff;
    border:0px;

}
& tr:nth-of-type(even) {
    background:#E7E7E7;
    border:0px;
}
& input {
    border:0px;
    border-bottom:2px solid black;
    background:transparent;
    padding:10px;
}
& input[type=submit] {
    width:100%;
    height:100%;
background:black;
text-transform:uppercase;
color:#fff;
font-weight:bold;
}
`




/*PAGINATION*/ 

export default class App extends Component {
  

    state = {
        employees: []
    };
    componentDidMount() {
    axios.get('http://localhost:5000/Employees').then((response) => {
        this.setState({
            employees: response.data
        })
    })
    }

    
    
    render() {
  
    return (
        <Main>
            <Header>
                <h1>Metricell</h1>

            </Header>
            <FormContainerAll>
                <FormContainerLeft>
                    <img src="https://images.unsplash.com/photo-1572883454114-1cf0031ede2a?crop=entropy&cs=tinysrgb&fm=jpg&ixlib=rb-1.2.1&q=80&raw_url=true&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687" alt="form_main"/>
                </FormContainerLeft>
                <FormContainerRight>
                    <ResultsTable id="listingTable">
                        <thead>
                            <th>Nombre</th>
                            <th>Value</th>
                            <th>Opciones</th>
                        </thead>
                        <tbody>
                            <tr>
                                <td><input type="text" name="value" placeholder="Introduce el nombre" /> </td>
                                <td> <input type="text" name="value" placeholder="Introduce el valor" /> </td>
                                <td> <input type="submit" value="Insertar" /> </td>
                            </tr>
                            {this.state.employees.map((employees: any) => (
                            <tr>
                                <td key="{employees.name}_name" > {employees.name} </td>
                                <td key="{employees.value}_value" > {employees.value} </td>
                                    <td> <button value="{employees.value}"> Borrar </button> </td>
                            </tr>
                            ))}
                            
                        </tbody>
                    </ResultsTable>
                </FormContainerRight>
                </FormContainerAll>
        </Main>
    );
    }

}
