import React, { Component, useState } from 'react';
import styled from 'styled-components';
import './stylesMain.css';
import axios from 'axios';

const Main = styled.section`
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:center;
    font-family:'Calibri';
    background:url('./Media/form-template.jpg');
    width:100vw;
    height:auto;
    background:#112337;
`
const Header = styled.header`
& {
    display:flex;
    align-items:center;
    justify-content:flex-start;
    font-family:'Calibri';
    background:#112337;
    width:100vw;
    height:100px;
}
& h1 {
    font-size:3rem;
    width:500px;
    display:flex;
    justify-content:center;
    align-items:center;
    color:#fff;
    text-transform:uppercase;
    letter-spacing:-.2rem;
}
`
const FormContainerAll = styled.div`
& {
    width:100vw;
    background:#112337;
    height:auto;
    display:flex;
    flex-direction:row;
}`
const FormContainerLeft = styled.div`
& {
    width:20vw;
    height:auto;
}
& img {
    width:100%;
    height:90vh;
    object-fit:cover;
    object-position:center;

}
`
const FormContainerRight = styled.div`
& {
    width:80vw;
    height:100vh;
    background:#112337;
    background:red;
}
`
const ResultsTable= styled.table`
& {
    display:table;
    font-family:'Calibri';
    background:#ffff;
    width:20vw;
    height:auto;
}
& th {
    background:black;
    color:white;
    padding:15px;
    font-size:1.3rem;
    text-transform:uppercase;
    border:0px;
    }
& td {
    font-size:1.1rem;
    align-items:center;
    text-align:center;
    padding:15px;
    border:0px;
    font-weight:bold;
}
& tr:nth-of-type(odd) {
    background:#fff;
    border:0px;

}
& tr:nth-of-type(even) {
    background:#E7E7E7;
    border:0px;
}
`

var current_page = 1;
var records_per_page = 3;
/* API CALL*/
const employee = useState(0);
state = {
    employees: []
};

axios.get('http://localhost:5000/Employees').then((response) => {
        this.setState({
            employees: response.data
        })
})


/*PAGINATION*/ 
function prevPage() {
    if (current_page > 1) {
        current_page--;
        changePage(current_page);
    }
}

function nextPage() {
    if (current_page < numPages()) {
        current_page++;
        changePage(current_page);
    }
}

function changePage(page) {
    var btn_next = document.getElementById("btn_next");
    var btn_prev = document.getElementById("btn_prev");
    var listing_table = document.getElementById("listingTable");
    var page_span = document.getElementById("page");

    // Validate page
    if (page < 1) page = 1;
    if (page > numPages()) page = numPages();

    listing_table.innerHTML = "";

    for (var i = (page - 1) * records_per_page; i < (page * records_per_page) && i < employees.length; i++) {
        listing_table.innerHTML += employees.adName + "<br>";
    }
    page_span.innerHTML = page + "/" + numPages();

    if (page == 1) {
        btn_prev.style.visibility = "hidden";
    } else {
        btn_prev.style.visibility = "visible";
    }

    if (page == numPages()) {
        btn_next.style.visibility = "hidden";
    } else {
        btn_next.style.visibility = "visible";
    }
}

function numPages() {
    return Math.ceil(employees.length / records_per_page);
}

export default class App extends Component {

  

    
    
    render() {
    return (
        <Main>
            <Header>
                <h1>Metricell</h1>

            </Header>
            <FormContainerAll>
                <FormContainerLeft>
                    <img src="https://images.unsplash.com/photo-1572883454114-1cf0031ede2a?crop=entropy&cs=tinysrgb&fm=jpg&ixlib=rb-1.2.1&q=80&raw_url=true&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687" alt="form_main"/>
                </FormContainerLeft>
                <FormContainerRight>
                    <ResultsTable id="listingTable">
                        <thead>
                            <th>Nombre</th>
                            <th>Value</th>
                        </thead>
                        <tbody>
                            {this.state.employees.map((employees: any) => (
                            <tr>
                                <td key="{employees.name}_name" > {employees.name} </td>
                                <td key="{employees.value}_value" > {employees.value} </td>
                            </tr>
                        ))}
                        </tbody>
                       
                        <a href="javascript:prevPage()" id="btn_prev">Prev</a>
                        <a href="javascript:nextPage()" id="btn_next">Next</a>
               
                    </ResultsTable>
                </FormContainerRight>
                </FormContainerAll>
        </Main>
    );
    }

}
