export function Script() {

    window.addEventListener('load', () => {
        console.log("test");
        var deleteButton = document.getElementsByClassName("borrar");
        for (var i = 0; i < deleteButton.length; i++) {
            /*deleteButton[i].style.display = "none";*/
            let buttonValue = deleteButton[i].value;
            deleteButton[i].addEventListener('click', (buttonValue) => {
                console.log(buttonValue);
            }
            );
            console.log(deleteButton[i].value);
        }
    })
    return null;
}
export default Script;
