import React, { Component } from 'react';
import styled from 'styled-components';
import './stylesMain.css';
import axios from 'axios';

const Main = styled.section`
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:center;
    font-family:'Calibri';
    background:url('./Media/form-template.jpg');
    width:100vw;
    height:auto;
`
const Header = styled.header`
& {
    display:flex;
    align-items:center;
    justify-content:flex-start;
    font-family:'Calibri';
    background:#ffff;
    width:100vw;
    height:100px;
}
& h1 {
    font-size:4rem;
    width:500px;
    display:flex;
    justify-content:center;
    align-items:center;
}
`
const FormContainerAll = styled.div`
& {
    width:100vw;
    background:red;
    height:auto;
    flex-direction:row;
}`
const FormContainerLeft = styled.div`
& {
    width:50vw;
    height:100vh;
    background:green;
}`
const FormContainerRight = styled.div`
& {
    width:50vw;
    height:100vh;
    background:blue;
}
`
const ResultsTable= styled.table`
& {
    display:table;
    font-family:'Calibri';
    background:#ffff;
    width:50vw;
    height:auto;
}
& td {
    font-size:.8rem;
    align-items:center;
    text-align:center;
}
& tbody:nth-of-type(odd) {
    background:white;
}
& tbody:nth-of-type(even) {
    background:#E7E7E7;
}
`
const img_route = "./Media/form-template.jpg";

export default class App extends Component {


    state = {
        employees: []
    }
    componentDidMount() {
        axios.get('http://localhost:5000/Employees').then((response) => {
            this.setState({
                employees: response.data
            })
        })
    }
    
    render() {
    return (
        <Main>
            <Header>
                <h1>Metricell</h1>

            </Header>
            <FormContainerAll>
                <FormContainerLeft>
                    <img src={img_route} alt="form_main"/>
                </FormContainerLeft>
                <FormContainerRight>
                    <ResultsTable>
                        <thead>
                            <th>Nombre</th>
                            <th>Value</th>
                        </thead>
                        {this.state.employees.map((employees: any) => (
                         <tbody>
                                <td key="{employees.name}" > {employees.name} </td>
                                <td key="{employees.value}" > {employees.value} </td>
                         </tbody>
                        ))}
               
                    </ResultsTable>
                </FormContainerRight>
                </FormContainerAll>
        </Main>
    );
  }
}
