import React, { Component } from 'react';
import styled from 'styled-components';
import './stylesMain.css';
import axios from 'axios';

const Main = styled.section`
    display:flex;
    flex-direction:column;
    align-items:flex-start;
    justify-content:center;
    font-family:'Calibri';
    background:#f2f2f2;
    width:100vw;
    height:auto;
`
const Header = styled.header`
& {
    display:flex;
    align-items:center;
    justify-content:flex-start;
    font-family:'Calibri';
    background:#ffff;
    width:100vw;
    height:100px;
}
& h1 {
    font-size:4rem;
    width:500px;
    display:flex;
    justify-content:center;
    align-items:center;
}
`
const ResultsContainer = styled.table`
& {
    display:table;
    font-family:'Calibri';
    background:#ffff;
    width:50vw;
    height:50vh;
}
& td {
    font-size:.8rem;
    align-items:center;
    text-align:center;
}
`


export default class App extends Component {

    state = {
        employees: []
    }
    componentDidMount() {
        axios.get('http://localhost:5000/Employees').then((response) => {
            this.setState({
                employees: response.data
            })
        })
    }
    render() {
    return (
        <Main>
            <Header>
                <h1>Metricell</h1>

            </Header>
            <ResultsContainer>
                <tr>
                    <th>Nombre</th>
                    <th>Value</th>
                </tr>
                {this.state.employees.map((employees: any) => (
                 <tr>
                        <td key="{employees.name}" > {employees.name} </td>
                        <td key="{employees.value}" > {employees.value} </td>
                 </tr>
                ))}
               
            </ResultsContainer>
        </Main>
    );
  }
}
