import React, { Component, useState, setState } from 'react';
import styled from 'styled-components';
import './stylesMain.css';
import { Script } from './assets/app.js'
import axios from 'axios';
import { InsertForm } from './Component/insert.jsx';
import { modalControls } from './Component/modalControls';
import { fetchCall } from './Component/config';

modalControls();
fetchCall();


const Main = styled.section`
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:center;
    font-family:'Calibri';
    background:url('./Media/form-template.jpg');
    width:100vw;
    height:auto;
    overflow:hidden;
    background:#112337;
`
const Header = styled.header`
& {
    display:flex;
    align-items:center;
    justify-content:flex-start;
    font-family:'Calibri';
    background: url('https://images.unsplash.com/photo-1572883454114-1cf0031ede2a?crop=entropy&cs=tinysrgb&fm=jpg&ixlib=rb-1.2.1&q=80&raw_url=true&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687'), #112337;
    background-position:left;
    background-repeat:no-repeat;
    width:100vw;
    height:100px;
}
& h1 {
    font-size:3rem;
    width:500px;
    display:flex;
    justify-content:center;
    align-items:center;
    color:#fff;
    text-transform:uppercase;
    letter-spacing:-.2rem;
}
`
const FormContainerAll = styled.div`
& {
    width:100vw;
    background:#112337;
    height:auto;
    display:flex;
    flex-direction:row;
    overflow:hidden;
}`
const FormContainerLeft = styled.div`
& {
    width:20vw;
    height:auto;
display:none;
}
& img {
    width:100%;
    height:90vh;
    object-fit:cover;
    object-position:center;

}
`
const FormContainerRight = styled.div`
& {
    width:100vw;
    height:90vh;
    background:#112337;
    overflow-y:scroll;
overflow-x:hidden;
}
`
const ResultsTable= styled.table`
& {
    display:table;
    font-family:'Calibri';
    background:#ffff;
    width:100%;
    height:auto;
    padding-bottom:20px;
}
& th {
    background:black;
    color:white;
    padding:10px;
    font-size:1.3rem;
    text-transform:uppercase;
    border:0px;
    }
& td {
    font-size:1.1rem;
    align-items:center;
    text-align:center;
    padding:10px;
    border:0px;
    font-weight:bold;
}
& tr:nth-of-type(odd) {
    background:#fff;
    border:0px;

}
& tr:nth-of-type(even) {
    background:#E7E7E7;
    border:0px;
}
& tr:last-of-type {
   
}
& input {
    border:0px;
    border-bottom:1px solid black;
    background:transparent;
    padding:10px;
    font-size:1rem;
    outline:none;
}
& input[type=submit] {
    padding:10px;
    background:black;
    text-transform:uppercase;
    color:#fff;
    font-weight:bold;    
}
& button {
    border:0px;
    border-bottom:2px solid black;
    padding:10px;
    background:black;
    text-transform:uppercase;
    color:#fff;
    font-weight:bold;
}
`
const ModalContainer = styled.div`
 & {
    width:100vw;
    height:100vh;
    background: rgba(0,0,0, 0.46);
    position:fixed;
    z-index:99;
    top:0;
    display:none;
    justify-content:center;
    align-items:center;
}
& div {
    background: #fff;
    padding:20px;
    border-radius:2px;
}
& div h1 {
    font-size:2rem;
    letter-spacing:-.1rem;
}
& div button {
    background:black;
    text-transform:uppercase;
    color:white;
    padding:10px;
    border:0px;
    width:100%;
    font-size:1.5rem;
    margin-top:50px;
    cursor:pointer;
}
`


function deleteClick(props) {
    console.log(props);
    fetchData('/delete/id=' + props).then(() => {
        console.log("deleted element");
        showModal("Se ha actualizado el valor del usuario, el nuevo nombre es:  " + name);
    });
}
function updateValue(name, value) {
    console.log(name + value);
    fetchData('/modifyvalue/name=' + name + '&value=' + value).then(() => {
        console.log("changed element");
        showModal("Se ha actualizado el usuario. El nuevo valor es:  " + value);
    });
}
function updateName(name, value) {
    console.log(name + value);
    fetchData('/modifyname/name=' + name + '&value=' + value).then(() => {
        console.log("changed name");
        showModal("Se ha actualizado el valor del usuario, el nuevo nombre es:  " + name );
        
    });
}
var userValue = 0;
var userName = '';
const getInputValue = (event) => {
    userValue = event.target.value;
    console.log(userValue);
    return userValue;
};
const getInputName = (event) => {
    userName = event.target.value;
    console.log(userName);
    return userName;
};





export class App extends Component {
     state = {
         employees: [],
         val: ''
     };
     componentDidMount() {
         axios.get('http://localhost:5000/Employees').then((response) => {
             this.setState({
                 employees: response.data
             })
         })
    }
     render(){
    return (
        <Main>
            <Header>
                <h1>Metricell</h1>
            </Header>
            <FormContainerAll>
                <FormContainerLeft>
                    <img src="https://images.unsplash.com/photo-1572883454114-1cf0031ede2a?crop=entropy&cs=tinysrgb&fm=jpg&ixlib=rb-1.2.1&q=80&raw_url=true&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687" alt="form_main"/>
                </FormContainerLeft>
                <FormContainerRight>
                    <ResultsTable id="listingTable">
                        <thead>
                            <tr>
                                <th>Nombre</th>
                                <th>Value</th>
                                <th colSpan="3">Opciones</th>
                            </tr>
                            <InsertForm></InsertForm>
                        </thead>
                        <tbody id="tableBody">
                            
                           
                         
                            {this.state.employees.map((employees: any) => (
                            <tr>
                                    <td key={employees.name} id={employees.name}>
                                        <input
                                            type="text"
                                            placeholder={employees.name}
                                            onChange={getInputName}
                                        />
                                    </td>
                                    <td
                                        key={employees.value}
                                    >
                                        <input
                                            type="number"
                                            placeholder={employees.value}
                                            onChange={getInputValue}
                                        />
                                    </td>
                                    <td key="option_update"> <button id={employees.value} value={employees.value} onClick={() => updateName(userName, employees.value)}> Actualizar nombre </button> <button id={employees.value} value={employees.name} onClick={() => updateValue(employees.name, userValue)}> Actualizar valor </button>  <button id={employees.value} className="borrar" value={employees.value} onClick={() => deleteClick(employees.value)} > Borrar </button></td>
                                  

                                   
                                
                                   
                            </tr>
                            ))
                                

                            }
                            
                        </tbody>
                    </ResultsTable>
                </FormContainerRight>
            </FormContainerAll>
            <ModalContainer id="modalContainer">
                <div>
                    <h1> �Muchas gracias por la oportunidad! Realizado por David Mart�n Concepci�n </h1>
                    <button onClick={closeModal}>Continuar </button>
                </div>
                 
            </ModalContainer>
            <Script></Script>
        </Main>
    );
}
    }


export default App;
