export function Script() {

    window.addEventListener('load', () => {
        function fetchData(url_api) {
            return new Promise((resolve, reject) => {
                let xhttp = window.XMLHttpRequest ? new XMLHttpRequest() : new window.ActiveXObject("Microsoft.XMLHTTP");
                xhttp.open('GET', url_api, true);
                xhttp.setRequestHeader('X-Requested-With', 'XMLHttpRequest', 'Content-Type');
                xhttp.setRequestHeader('Access-Control-Allow-Origin', '*');
                xhttp.onreadystatechange = function () {
                    if (xhttp.readyState === 4) {
                        (xhttp.status === 200)
                            ? resolve(JSON.parse(xhttp.responseText))
                            : reject(new Error('Error ', url_api))
                    }
                }
                xhttp.send();
            });

        }
        
        console.log("test");
        var deleteButton = document.getElementsByClassName("borrar");
        for (var i = 0; i < deleteButton.length; i++) {
            
            /*deleteButton[i].style.display = "none";*/
            var buttonValue  = deleteButton[i].value;
            deleteButton[i].addEventListener('click', (buttonValue) => {
                fetchData("http://localhost:5000/delete/id=" + buttonValue).then(element => {
                    console.log("deleted valued"+ buttonValue);
                })
                
            });
            console.log(deleteButton[i].value);
        }
    })
    return null;
}
export default Script;
