import React, { useRef } from 'react';

function deleteClick(props) {
    console.log(props);
    fetchData('/delete/id=' + props).then(() => {
        console.log("deleted element");
    });
}
function updateValue(props) {
    console.log(props);
    fetchData('/modifyvalue/id=' + props).then(() => {
        console.log("deleted element");
    });
}

export function UpdateForm(employees) {

    
    return (
        <>
            {this.state.employees.map((employees: any) => (
                <tr>
                    <td key={employees.name} id={employees.name}> {employees.name} </td>
                    <td
                        key={employees.value}
                    > {employees.value} </td>
                    <td key="option_update"> <button value={employees.name} onClick={() => updateValue(employees.name, employees.value)}> Actualizar valor </button> </td>
                    <td key="option_delete"> <button id={employees.value} className="borrar" value={employees.value} onClick={() => deleteClick(employees.value)} > Borrar </button> </td>


                </tr>
            ))


            }
        </>
    );
}
export default UpdateForm;
